SELECT cs.su_idx , su_name, pc_problem, pc_answer, pc_answer1, pc_answer2,pc_answer3,pc_answer4, pc_explan, cps.pc_idx, ps_idx, ps_date 
FROM cbt_problem_save cps LEFT OUTER JOIN cbt_problem_create cpc ON cps.pc_idx = cpc.pc_idx LEFT OUTER JOIN cbt_subject cs ON cpc.su_idx = cs.su_idx 
WHERE cps.mb_id=#{param1}

SELECT cs.su_idx , su_name, pc_problem, pc_answer, pc_answer1, pc_answer2,pc_answer3,pc_answer4, pc_explan, cps.pc_idx, ps_idx, ps_date 
FROM cbt_problem_save cps LEFT OUTER JOIN cbt_problem_create cpc ON cps.pc_idx = cpc.pc_idx LEFT OUTER JOIN cbt_subject cs ON cpc.su_idx = cs.su_idx 
WHERE cps.mb_id=#{param1} 


		AND cs.su_idx=#{param2}
